<?php
$class_name = 'b-banner';
if (!empty($block['className'])) {
    $class_name .= ' ' . $block['className'];
}
if (!empty($block['align'])) {
    $class_name .= ' align' . $block['align'];
}
$anchor = '';
if (!empty($block['anchor'])) {
    $anchor = 'id="' . esc_attr($block['anchor']) . '"';
}

$title = get_field('title');
$desc = get_field('desc');
$kv = get_field('kv');
$buttons = get_field('buttons');
?>

<section class="<?php echo esc_attr($class_name); ?>" <?php echo $anchor; ?>>
    <div class="b-banner__container">
        <div class="b-banner__content">
            <?php if ($title || $desc): ?>
                <div class="b-banner__text">
                    <?php if ($title): ?>
                        <h2 class="b-banner__title"><?php echo $title; ?></h2>
                    <?php endif; ?>

                    <?php if ($desc): ?>
                        <p class="b-banner__desc"><?php echo $desc; ?></p>
                    <?php endif; ?>
                </div>
            <?php endif; ?>

            <?php if (!empty($buttons)): ?>
                <div class="b-banner__buttons">
                    <?php
                    $button_index = 0;
                    foreach ($buttons as $button_row):
                        $link = $button_row['link'] ?? [];
                        if (empty($link['url']) || empty($link['title'])) {
                            continue;
                        }
                        $target = !empty($link['target']) ? $link['target'] : '_self';
                        $button_class = 'b-banner__button';
                        // First button is primary, others are secondary
                        if ($button_index === 0) {
                            $button_class .= ' b-banner__button--primary';
                        } else {
                            $button_class .= ' b-banner__button--secondary';
                        }
                        $button_index++;
                    ?>
                        <a href="<?php echo esc_url($link['url']); ?>" target="<?php echo esc_attr($target); ?>"
                            class="<?php echo esc_attr($button_class); ?>">
                            <?php echo esc_html($link['title']); ?>
                        </a>
                    <?php endforeach; ?>
                </div>
            <?php endif; ?>
        </div>
        <?php if ($kv): ?>
            <div class="b-banner__image">
                <?php echo wp_get_attachment_image($kv, 'full', false, ['class' => 'b-banner__image-img']); ?>
            </div>
        <?php endif; ?>
    </div>
