<?php
$class_name = 'b-slider-fu';
if (!empty($block['className'])) {
    $class_name .= ' ' . $block['className'];
}
if (!empty($block['align'])) {
    $class_name .= ' align' . $block['align'];
}
$anchor = '';
if (!empty($block['anchor'])) {
    $anchor = 'id="' . esc_attr($block['anchor']) . '"';
}

// Get slide width from ACF field
$slide_width = get_field('slide_width');
if (empty($slide_width)) {
    $slide_width = 1.6; // Default value
}

// Query funkcje CPT posts
$args = [
    'post_type' => 'funkcje',
    'posts_per_page' => -1,
    'post_status' => 'publish',
    'orderby' => 'menu_order',
    'order' => 'ASC',
];

$funkcje_query = new WP_Query($args);

if (!$funkcje_query->have_posts()) {
    if (is_admin()) {
        echo '<p>' . esc_html__('Dodaj przynajmniej jedną funkcję (CPT: funkcje), aby wyświetlić karuzelę.', 'care4kids') . '</p>';
    }
    return;
}
?>

